// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/// @title IBadgeRegistry
/// @notice Interface for interacting with the BadgeRegistry contract.
interface IBadgeRegistry {
    // -------- Views --------
    function soulbound(uint256 badgeId) external view returns (bool);

    function badgeTier(uint256 badgeId) external view returns (uint8);

    function expiryOf(
        address user,
        uint256 badgeId
    ) external view returns (uint256);

    function hasBadge(
        address user,
        uint256 badgeId
    ) external view returns (bool);

    function hasAny(
        address user,
        uint256[] calldata badgeIds
    ) external view returns (bool);

    function tierOf(
        address user,
        uint256[] calldata considerIds
    ) external view returns (uint8);

    // -------- Mutative (apps/admins) --------
    function mintBadge(address user, uint256 badgeId, uint256 expiry) external;

    function renewBadge(
        address user,
        uint256 badgeId,
        uint256 newExpiry
    ) external;

    function revokeBadge(address user, uint256 badgeId) external;

    function selfBurn(uint256 badgeId) external;

    // -------- ERC1155 compatibility --------
    function balanceOf(
        address account,
        uint256 id
    ) external view returns (uint256);

    function safeTransferFrom(
        address from,
        address to,
        uint256 id,
        uint256 amount,
        bytes calldata data
    ) external;

    function safeBatchTransferFrom(
        address from,
        address[] calldata tos,
        uint256 id
    ) external;
}

project/contracts/utils/GatingLib.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import { IBadgeRegistry as BadgeRegistry } from "../interfaces/IBadgeRegistry.sol";
library GatingLib {
    /// @notice Returns true if user has an active badge
    function hasBadge(
        BadgeRegistry reg,
        address user,
        uint256 badgeId
    ) internal view returns (bool) {
        if (address(reg) == address(0)) return false; // gracefully handle missing registry
        return reg.hasBadge(user, badgeId);
    }

    /// @notice Returns true if user has any badge among given ids
    function hasAny(
        BadgeRegistry reg,
        address user,
        uint256[] memory badgeIds
    ) internal view returns (bool) {
        if (address(reg) == address(0)) return false;
        return reg.hasAny(user, badgeIds);
    }

    /// @notice Returns user's tier (0 if no active badges)
    function tierOf(
        BadgeRegistry reg,
        address user,
        uint256[] memory considerIds
    ) internal view returns (uint8) {
        if (address(reg) == address(0)) return 0;
        return reg.tierOf(user, considerIds);
    }

    /// @notice Quick helper: check if user meets at least `minTier`
    function meetsTier(
        BadgeRegistry reg,
        address user,
        uint256[] memory considerIds,
        uint8 minTier
    ) internal view returns (bool) {
        if (address(reg) == address(0)) return false;
        return reg.tierOf(user, considerIds) >= minTier;
    }
}
