// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/// @title IVerseProfile
/// @notice Interface for interacting with the VerseProfile identity contract.
interface IVerseProfile {
    // -------- Views --------
    function getProfile(
        uint256 verseId
    )
        external
        view
        returns (
            address owner,
            string memory verseHandle,
            string memory metadataURI,
            bytes32 ensNamehash,
            uint64 createdAt
        );

    function verseIdByHandle(string calldata handle) external view returns (uint256);

    function getAppNickname(uint256 verseId, bytes32 appId) external view returns (string memory);

    function verseIdByAppNickname(bytes32 appId, string calldata nickname) external view returns (uint256);

    function hasProfile(address user) external view returns (bool);

    function getDisplayHandle(uint256 verseId, bytes32 appId) external view returns (string memory);

    function profileOf(address user) external view returns (uint256);

    // -------- Mutative --------
    function createProfile(
        string calldata verseHandle,
        string calldata metadataURI,
        bytes32 ensNamehash
    ) external returns (uint256 verseId);

    function setVerseHandle(uint256 verseId, string calldata newHandle) external;

    function setMetadataURI(uint256 verseId, string calldata newURI) external;

    function linkENS(uint256 verseId, bytes32 namehash) external;

    function transferOwnershipOfProfile(uint256 verseId, address newOwner) external;

    function setAppNickname(uint256 verseId, bytes32 appId, string calldata nickname) external;
}

project/contracts/interfaces/IVerseReput.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/// @title IVerseReputationHub
/// @notice Minimal interface for logging reputation-related events in the Verse.
interface IVerseReputationHub {
    /// @notice Log that a profile has completed an action in an app.
    /// @param verseId The Verse profile ID of the user.
    /// @param token   The payment/reward token (0 = native).
    /// @param amount  Amount earned (can be 0 if not relevant).
    function logCompleted(uint256 verseId, address token, uint256 amount) external;

    /// @notice Log that a profile has cancelled an action in an app.
    /// @param verseId The Verse profile ID of the user.
    function logCancelled(uint256 verseId) external;

    /// @notice Get detailed activity for a profile in a given app.
    /// @param verseId The Verse profile ID.
    /// @param appId   The apps identifier (keccak256("HireCore"), etc.).
    /// @param token   Token address to check earned amounts.
    /// @return completed Count of completed actions.
    /// @return cancelled Count of cancelled actions.
    /// @return earned    Total tokens earned for this token.
    function getActivity(
        uint256 verseId,
        bytes32 appId,
        address token
    ) external view returns (uint64 completed, uint64 cancelled, uint256 earned);

    /// @notice Get just the raw counts (no token info).
    function getRawCounts(uint256 verseId, bytes32 appId)
        external
        view
        returns (uint64 completed, uint64 cancelled);
}
